import React from "react";
import Articles from "./Articles";

class Novels extends React.Component {

    state = {
        datum: [{title: "", date: "", link: ""}]
    }

    componentDidUpdate(nextProps){
        const{ data } = this.props;
        if(nextProps.novs !== data.novs){
            nextProps.novs.map((item, i) => {
                this.setState({datum: nextProps.novs})
            })
        }
    }

    render() {
        console.log(this.props.novs);
        return (
            <div className="novelsMain">           
                {this.state.datum.map((item, i) => {
                    return <div key={i}> {item.title} </div>
                })}
                <Articles />
            </div>
        );
    }
}
export default Novels;
