import React from "react";
import ReactTwitchEmbedVideo from "react-twitch-embed-video";

export default class TwitchArticles extends React.Component {
    constructor(props){
        super(props);
        this.handleClick = this.handleClick.bind(this);

    }

    state = {
        channel1: "monstercat",
        channel2: "kamikazecashew"
    }

    handleClick (e, id) {
        console.log(e.target.id)
        if(e.target.id === 1){
            console.log(id)
            this.setState({channel1: id})
        }
        else if(e.target.id === 2){
            this.setState({channel2: id})
        }
    }

    render() {
        var yeet = [];
        //pushes props into array on relaod and displays, emptying on rerender
        yeet.push(this.props.usrs.map((item, i) => {
            return <div className="post" key={i}>
                <h4>{item.name}</h4>
                <p>{item.title}</p>
                <button id="1" onClick={(e) => this.handleClick(e, item.name)}>Stream 1</button>
                <button id="2" onClick={(e) => this.handleClick(e, item.name)}>Stream 2</button>
                </div>
        }))
        return (
            <div>
                {yeet}
                <ReactTwitchEmbedVideo channel={this.state.channel1} />
                <ReactTwitchEmbedVideo channel={this.state.channel2} />
            </div>

        );
    }

}
