import React from "react";
import ReactTwitchEmbedVideo from "react-twitch-embed-video";
import root from 'window-or-global';

export default class TwitchArticles extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick1 = this.handleClick1.bind(this);

    }

    state = {
        channel1: "monstercat",
    }

    handleClick1(e, id) {
        var myNode = document.getElementById("twitch-embed");
        myNode.innerHTML = '';

        this.embed = null;
        this.embed = new window.Twitch.Embed("twitch-embed", {
            width: 840,
            height: 480,
            channel: id
        });
    }
    componentDidMount() {
        const script = document.createElement('script');
        script.setAttribute('src', 'https://embed.twitch.tv/embed/v1.js');

        script.addEventListener('load', () => {
            this.embed = new root.Twitch.Embed("twitch-embed", {
                width: 840,
                height: 480,
                channel: 'kamikazecashew'
            })
        });
        document.body.appendChild(script);
    }




    render() {
        var yeet = [];
        //pushes props into array on relaod and displays, emptying on rerender
        yeet.push(this.props.usrs.map((item, i) => {
            return <div className="post" key={i}>
                <h4>{item.name}</h4>
                <p>{item.title}</p>
                <button id="1" onClick={(e) => this.handleClick1(e, item.name)}>Stream 1</button>
            </div>
        }))
        console.log(this.state)
        return (
            <div>
                {yeet}
            </div>

        );
    }

}
