import React from "react";

export default class Twitch extends React.Component {

    UNSAFE_componentWillMount() {
        let url = "https://api.twitch.tv/helix/users/follows?from_id=455825055"
        let usrs = [];
        let expectedLength = 0;
        fetch(url, {
            method: 'GET',
            headers: {
                'Client-ID': 'rc8uqc4k9iv82b8l339oymibbd3nkb'
            }
        }).then(Response => Response.json())
            .then(data => {
                expectedLength = data.data.length;
                for (let i = 0; i < data.data.length; i++) {
                    let strURL = "https://api.twitch.tv/helix/streams?user_login=" + data.data[i].to_name;
                    fetch(strURL, {
                        method: 'GET',
                        headers: {
                            'Client-ID': 'rc8uqc4k9iv82b8l339oymibbd3nkb'
                        }
                    })
                    .then(Response => Response.json())
                    .then(data => {usrs.push(data)})
                }
                return usrs;
            })
            .then(arr => {
                if(arr.length === expectedLength){
                    console.log(arr);
                }
            })

    }

    render() {
        return (
            <div className="twitchMain">
                <h4>Twitch</h4>

            </div>

        );
    }

}
